import React, { useState } from 'react';
import UploadResume from './UploadResume';
import AIEnhanceButton from './AIEnhanceButton';
import axios from 'axios';

function ResumeForm() {
  const [resume, setResume] = useState({
    name: '',
    experience: '',
    education: '',
    skills: ''
  });


function ResumeForm() {
  const [summary, setSummary] = useState("");   // this holds user input
  const [loading, setLoading] = useState(false); // loading flag

  const handleEnhance = async () => {
    try {
      setLoading(true);
      const response = await axios.post("http://localhost:8000/enhance", {
        summary: summary,
      });
      setSummary(response.data.summary); // update with enhanced summary
    } catch (err) {
      alert("Something went wrong!");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form className="resume-form">
      <label htmlFor="summary">Summary</label>
      <textarea
        id="summary"
        value={summary}
        onChange={(e) => setSummary(e.target.value)}
        placeholder="Enter your summary here..."
      />

      <button
        type="button"
        onClick={handleEnhance}
        disabled={loading}
        className="call-to-action"
      >
        {loading ? "Enhancing..." : "Enhance with AI"}
      </button>
    </form>
  );
}


  const handleChange = (e) => {
    setResume({ ...resume, [e.target.name]: e.target.value });
  };

  const saveResume = async () => {
    try {
      await axios.post('http://localhost:8000/save-resume', resume);
      alert('Resume saved!');
    } catch (error) {
      alert('Failed to save resume.');
    }
  };

  const downloadResume = () => {
    const blob = new Blob([JSON.stringify(resume, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'resume.json';
    a.click();
  };

  return (
    <div className="space-y-8">
      <UploadResume setResume={setResume} />

      {/* Name Section */}
      <section id="name">
        <label className="block font-semibold text-gray-700 mb-1">Name</label>
        <input
          type="text"
          name="name"
          value={resume.name}
          onChange={handleChange}
          className="w-full border rounded px-3 py-2 mb-2"
        />
        <AIEnhanceButton section="name" content={resume.name} setResume={setResume} resume={resume} />
      </section>

      {/* Experience Section */}
      <section id="experience">
        <label className="block font-semibold text-gray-700 mb-1">Experience</label>
        <textarea
          name="experience"
          value={resume.experience}
          onChange={handleChange}
          className="w-full border rounded px-3 py-2 mb-2"
          rows={4}
        />
        <AIEnhanceButton section="experience" content={resume.experience} setResume={setResume} resume={resume} />
      </section>

      {/* Education Section */}
      <section id="education">
        <label className="block font-semibold text-gray-700 mb-1">Education</label>
        <textarea
          name="education"
          value={resume.education}
          onChange={handleChange}
          className="w-full border rounded px-3 py-2 mb-2"
          rows={4}
        />
        <AIEnhanceButton section="education" content={resume.education} setResume={setResume} resume={resume} />
      </section>

      {/* Skills Section */}
      <section id="skills">
        <label className="block font-semibold text-gray-700 mb-1">Skills</label>
        <input
          type="text"
          name="skills"
          value={resume.skills}
          onChange={handleChange}
          className="w-full border rounded px-3 py-2 mb-2"
        />
        <AIEnhanceButton section="skills" content={resume.skills} setResume={setResume} resume={resume} />
      </section>

      {/* Buttons */}
      <div className="flex flex-wrap gap-4 pt-4">
        <button
          onClick={saveResume}
          className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded shadow"
        >
          Save Resume
        </button>
        <button
          onClick={downloadResume}
          className="bg-green-600 hover:bg-green-700 text-white px-6 py-2 rounded shadow"
        >
          Download Resume (.json)
        </button>
      </div>
    </div>
  );
}

export default ResumeForm;
