import React, { useState } from 'react';
import UploadResume from './UploadResume';
import AIEnhanceButton from './AIEnhanceButton';
import axios from 'axios';
import '../styles/ResumeForm.css';

function ResumeForm() {
  const [resume, setResume] = useState({ name: '', experience: '', education: '', skills: '' });

  const handleChange = (e) => {
    setResume({ ...resume, [e.target.name]: e.target.value });
  };

  const saveResume = async () => {
    await axios.post('http://localhost:8000/save-resume', resume);
    alert('Resume saved!');
  };

  const downloadResume = () => {
    const blob = new Blob([JSON.stringify(resume, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'resume.json';
    a.click();
  };

  return (
    <div>
      <UploadResume setResume={setResume} />
      <div>
        <label>Name</label>
        <input name="name" value={resume.name} onChange={handleChange} />
        <AIEnhanceButton section="name" content={resume.name} setResume={setResume} resume={resume} />
      </div>
      <div>
        <label>Experience</label>
        <textarea name="experience" value={resume.experience} onChange={handleChange} />
        <AIEnhanceButton section="experience" content={resume.experience} setResume={setResume} resume={resume} />
      </div>
      <div>
        <label>Education</label>
        <textarea name="education" value={resume.education} onChange={handleChange} />
        <AIEnhanceButton section="education" content={resume.education} setResume={setResume} resume={resume} />
      </div>
      <div>
        <label>Skills</label>
        <input name="skills" value={resume.skills} onChange={handleChange} />
        <AIEnhanceButton section="skills" content={resume.skills} setResume={setResume} resume={resume} />
      </div>
      <button onClick={saveResume}>Save Resume</button>
      <button onClick={downloadResume}>Download Resume (.json)</button>
    </div>

  );
}

export default ResumeForm;
