import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import './index.css';
import { FaUser, FaBriefcase, FaGraduationCap, FaTools } from 'react-icons/fa';
import { FiEdit } from 'react-icons/fi';
import ResumeForm from './components/ResumeForm';
import NamePage from './components/NamePage';
import ExperiencePage from './components/ExperiencePage';
import EducationPage from './components/EducationPage';
import SkillsPage from './components/SkillsPage';

function App() {
  return (
    <Router>
      <div className="App min-h-screen bg-gray-100 flex items-center justify-center">
        <div className="max-w-4xl mx-auto bg-white shadow-lg rounded-lg p-6">

          {/* Heading with Icon */}
          <div className="flex flex-col items-center min-h-screen justify-center">
            <h1 className="text-4xl font-bold text-blue-600 mb-4 flex items-center gap-3">
              <FiEdit className="text-blue-500 w-8 h-8" />
              Resume Editor
            </h1>

            {/* Centered Navbar */}
            <nav className="navbar flex gap-4">
              <Link to="/name" className="flex items-center text-blue-600 font-medium hover:underline gap-1">
                <FaUser /> Name
              </Link>
              <Link to="/experience" className="flex items-center text-blue-600 font-medium hover:underline gap-1">
                <FaBriefcase /> Experience
              </Link>
              <Link to="/education" className="flex items-center text-blue-600 font-medium hover:underline gap-1">
                <FaGraduationCap /> Education
              </Link>
              <Link to="/skills" className="flex items-center text-blue-600 font-medium hover:underline gap-1">
                <FaTools /> Skills
              </Link>
            </nav>
          </div>

          <Routes>
            <Route path="/" element={<ResumeForm />} />
            <Route path="/name" element={<NamePage />} />
            <Route path="/experience" element={<ExperiencePage />} />
            <Route path="/education" element={<EducationPage />} />
            <Route path="/skills" element={<SkillsPage />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
